mapper.select:
SELECT r.* FROM report r
INNER JOIN user u ON r.user_id = u.id
WHERE u.tutor = #{tutorId}


        ORDER BY r.create_time DESC


        ORDER BY r.create_time DESC


LIMIT #{offset}, #{limit}

mapper.update:
update report
set type = #{type}
where id = #{id}

update report
set status = #{status}
where id = #{id}

update report
set is_read = #{isRead}
where id = #{id}

update report
set is_pass = #{isPass}
where id = #{id}

update report
set score = #{score}
where id = #{id}
